import React, {Component} from 'react'
import PropTypes from 'prop-types'
import styled from 'styled-components'

/**
 * Styled version of the Fragment component
 * @see Fragment
 * @private
 * @type {StyledComponent}
 * @reactProps {Object} props
 *   The properties
 * @reactProps {(ReactNode|ReactNode[])} children
 * @reactProps {?Boolean} active
 * @reactProps {?string} animation
 */
const StyledFragment = styled.span`
  display: inline-block;
  white-space: pre-wrap;
  width: inherit;
  ${props =>
    props.animation ||
    `
      opacity: ${props.active ? 1 : 0}
  `};
`

/**
 * @private
 * @type {String}
 */
StyledFragment.displayName = 'StyledFragment'

/**
 * @private
 * @type {Object}
 * @prop {(ReactNode|ReactNode[])} children
 * @prop {?Boolean} active
 * @prop {?(String|Array)} animation
 */
StyledFragment.propTypes = {
  children: PropTypes.node,
  active: PropTypes.bool,
  animation: PropTypes.oneOfType([PropTypes.array, PropTypes.string])
}

export default StyledFragment

import React, {Component} from 'react'
import PropTypes from 'prop-types'
import styled from 'styled-components'

import StyledFragment from './fragment'

/**
 * Class for fragment.
 * @public
 *
 * @class Fragment @reactProps {Object} props
 *   The properties
 * @reactProps {(ReactElement|ReactElement[])} props.children
 * @reactProps {number} props.order
 * @reactProps {?Boolean} props.root
 * @reactProps {Object} context
 *   The context
 */
export default class Fragment extends Component {
  /**
   * @private
   * @return {{children: (ReactElement|ReactElement[]), animation: ?(String|Array), order: ?number}}
   *   Allowed propTypes for `<Fragment/>`
   */
  static get propTypes() {
    return {
      children: PropTypes.node.isRequired,
      animation: PropTypes.oneOfType([PropTypes.array, PropTypes.string]),
      order: PropTypes.number.isRequired
    }
  }

  /**
   * Get contextTypes
   * @private
   *
   * @return {{store: store, fragmentHost: number, hostedFragment: number}}
   */
  static get contextTypes() {
    return {
      store: PropTypes.object.isRequired,
      fragmentHost: PropTypes.number,
      hostedFragment: PropTypes.number
    }
  }

  /**
   * Sends context to decendants. Includes the app store to allow all deck
   * components to access the store without having to include its own logic.
   * @private
   *
   * @return {{hostedFragment: number}}
   */
  static get childContextTypes() {
    return {
      hostedFragment: PropTypes.number
    }
  }

  /**
   * Constructor
   * @private
   * @property {Object}                  props
   *   The properties
   * @property {(ReactNode|ReactNode[])} props.children
   * @property {number}                  props.order
   * @property {?Boolean}                props.root
   * @property {Object}                  context
   *   The context
   */
  constructor(props, context) {
    super(props, context)
  }

  /**
   * Gets the child context.
   * @private
   * @return {{hostedFragment: number}}
   *   The child context.
   */
  getChildContext() {
    return {
      hostedFragment: this.props.root ? this.props.order : 0
    }
  }

  /**
   * Before the fragments are mounted we need to tell the application about
   * their exitsance and behavior.
   * @private
   */
  componentWillMount() {
    const {order} = this.props
    const {fragmentHost, store, hostedFragment = 0} = this.context
    // Get the current host to allow extending it.
    const host = store.fragmentHosts[fragmentHost] || []

    // Handle root components. the root index is added to nested fragments
    // to allow them to be rendered before or after their parent fragment
    const fragmentOrder = order + hostedFragment

    // Only add new indexes to the queue.
    // Duplicate indexes will appear at the same time.
    // Root components are respected by definition.
    if (host.indexOf(fragmentOrder) < 0) {
      host.push(fragmentOrder)
    }

    // If no fragment has been defined with `0` we need to add a position to
    // our host
    if (host.length && host.indexOf(0) < 0) {
      host.push(0)
    }
    // Sort the fragment indexes and update the store
    store.fragmentHosts[fragmentHost] = host.sort((a, b) => a - b)
  }

  /**
   * @private
   * @return {number}
   *   The number of fragment indexes (steps to next slide)
   */
  get length() {
    return this.context.store.fragmentHosts[this.context.fragmentHost].length
  }

  /**
   * @private
   * @return {StyledFragment}
   *   The fragment including the entire logic
   */
  render() {
    const {order, children, animation} = this.props
    const {fragmentHost, store, hostedFragment = 0} = this.context
    // Define several flags to determine the acitve state
    // of the fragment.
    const isPrevious = fragmentHost < store.page
    const isNext = fragmentHost > store.page
    const fragmentOrder = order + hostedFragment
    const isZero = fragmentOrder === 0
    const isActivated = store.fragment >= fragmentOrder
    const isActive = isPrevious || (isNext ? isZero : isActivated)
    return (
      <StyledFragment active={isActive} animation={animation}>
        {children}
      </StyledFragment>
    )
  }
}

/**
 * Fragment root component. When using the fragmentRoot, nested Fragments start
 * after the parent. This is a curry version of Fragment.
 * @see    Fragment
 * @public
 * @class  FragmentRoot
 * @reactProps {Object} props
 * @reactProps {(ReactElement|ReactElement[])} props.children
 * @reactProps {number} props.order
 * @return {Fragment}
 *   A Fragment component as root
 */
export const FragmentRoot = props => <Fragment {...props} root />

import React, {Component} from 'react'
import PropTypes from 'prop-types'
import styled, {css} from 'styled-components'

/**
 * Class for image.
 * It loads an image as a background-image. The original image is still
 * rendered for accessibility reasons.
 * @public
 * @class Image
 * @reactProps {Object} props
 *   The properties
 * @reactProps {String} props.alt
 * @reactProps {String} props.src
 * @reactProps {?String} props.title
 */
class Image extends Component {
  /**
   * @private
   */
  state = {}
  /**
   * @private
   * @return {{alt: String, src: String, title: ?String}}
   *   Allowed propTypes for `<Fragment/>`
   */
  static get propTypes() {
    return {
      alt: PropTypes.string.isRequired,
      src: PropTypes.string.isRequired,
      title: PropTypes.string
    }
  }
  /**
   * Constructs the object.
   * @private
   * @param {Object} props
   *   The properties
   * @param {String} props.alt
   * @param {String} props.src
   * @param {?String} props.title
   */
  constructor(props) {
    super(props)
    this.getImage = this.getImage.bind(this)
    this.handleLoad = this.handleLoad.bind(this)
  }

  /**
   * Handles the load event of the image.
   * @private
   */
  handleLoad() {
    this.setState({
      loaded: true,
      height: this.image.height,
      width: this.image.width
    })
  }

  /**
   * Additional CSS rules for the mask
   * @private
   * @return {String|Array}
   */
  get mixin() {
    return css`
      --height: ${this.state.height}px;
      --width: ${this.state.width}px;
      background-image: url("${this.props.src}");
    `
  }

  /**
   * Assigns the image to `ref`.
   * @private
   * @param {ReactElement} el
   */
  getImage(el) {
    /**
     * @private
     */
    this.image = el
  }

  /**
   * @private
   * @return {Mask}
   *   An image nested inside a mask
   */
  render() {
    return (
      <Mask mixin={this.mixin}>
        <Img
          ref={this.getImage}
          src={this.props.src}
          alt={this.props.alt}
          title={this.props.title}
          onLoad={this.handleLoad}
        />
      </Mask>
    )
  }
}

/**
 * The image mask
 * @private
 * @type {StyledComponent}
 */
const Mask = styled.div`
  ${props => props.mixin || ''} background-size: cover;
  width: var(--width);
  height: var(--height);
`

/**
 * @private
 * @return {{mixin: ?(String|Array), children: (ReactElement|ReactElement[])}}
 *   Allowed propTypes for `<Mask/>`
 */
Mask.propTypes = {
  mixin: PropTypes.oneOfType([PropTypes.array, PropTypes.string]),
  children: PropTypes.instanceOf(Img)
}

/**
 * Styled img
 * @private
 * @type {StyledComponent}
 */
const Img = styled.img`
  ${props => (props.loaded ? 'display: none;' : '')};
`

/**
 * @private
 * @return {{loaded: Boolean, onLoad: Function, ref: Function, src: String, alt: String, title ?String)}}
 *   Allowed propTypes for `<Img/>`
 */
Img.propTypes = {
  loaded: PropTypes.boolean,
  onLoad: PropTypes.func,
  ref: PropTypes.func,
  src: PropTypes.string.isRequired,
  alt: PropTypes.string.isRequired,
  title: PropTypes.string
}

export default Image

import React, {Children, cloneElement} from 'react'
import PropTypes from 'prop-types'
import Master from './master'
import {Slot} from './components'

export default function create(master) {
  const {children, className} = master.props
  const Slide = props => {
    return (
      <Master {...props} content={props.children}>
        {Children.toArray(children).map((child, i) => {
          if (child.type === Slot) {
            return cloneElement(child, {
              key: `master-slot__${i}`,
              component: Slide[child.props.name]
            })
          }
          return child
        })}
      </Master>
    )
  }

  Children.toArray(children)
    .filter(child => child.type === Slot)
    .forEach(slot => {
      const {name} = slot.props
      Slide[name] = props => null
    })

  Slide.propTypes = {
    children: PropTypes.node,
    className: PropTypes.string
  }

  return Slide
}

import React, {Component, Children, cloneElement} from 'react'
import PropTypes from 'prop-types'
import uuid from 'uuid/v4'
import styled from 'styled-components'
import Slide from '@dekk/slide'
import Notes from '@dekk/speaker-notes'
import Fragment, {FragmentRoot} from '@dekk/fragment'
import Warning from './warning'
import {Slot, Static} from './components'

const invalid = (child, index, itemProps) => (
  <Warning
    {...itemProps}
    key={`slot_${index}`}
    type={
      (child.type && (child.type.displayName || child.type.name)) ||
      `"${child}"`
    }
    invalid
  />
)

const missing = (name, index, itemProps) => (
  <div key={`slot__${i}`} data-slot={name}>
    <Warning {...itemProps} missing />
  </div>
)

const onlyOrWarning = (only, child, index, itemProps) => {
  // Check if child is allowed
  if (only.includes(child.type)) {
    return child
  }
  // in case the child is a fragment, check all child elements
  if (child.type === Fragment || child.type === FragmentRoot) {
    return cloneElement(child, {
      children: Children.toArray(child.props.children).map(child =>
        onlyOrWarning(only, child)
      )
    })
  }
  return invalid(child, index, itemProps)
}

const notOrWarning = (not, child, index, itemProps) => {
  if (child.type === Fragment || child.type === FragmentRoot) {
    return cloneElement(child, {
      children: Children.toArray(child.props.children).map(child =>
        notOrWarning(only, child)
      )
    })
  }
  if (not.includes(child.type)) {
    return invalid(child, index, itemProps)
  }
  return child
}

class Master extends Component {
  render() {
    const {children, content} = this.props
    // All `Slot` instances
    const slots = Children.toArray(children).filter(
      child => child.type === Slot
    )

    // All `Static` instances
    const statics = Children.toArray(children).filter(
      child => child.type === Static
    )

    // Helper slots are filtered from the children.
    const helperSlots = [Notes]

    // Filtered children of the component.
    // Excludes helperSlots
    const onlyContent = Children.toArray(content).filter(
      child => !helperSlots.includes(child.type)
    )

    const filledStatics = statics.map((item, i) => (
      <div key={`static__${i}`} data-static={item.props.name}>
        {item.props.children}
      </div>
    ))

    const filledSlots = slots
      .map((item, i) => {
        const {only, not, required, component, name} = item.props
        const index = onlyContent.map(child => child.type).indexOf(component)
        const Element = item.props.as || 'div'

        if (index < 0) {
          if (required) {
            return missing(name, i, item.props)
          }
          return null
        }
        const children = Children.toArray(
          onlyContent[index].props.children
        ).map(
          (child, idx) =>
            // Check for `only` and `not` options
            // If neither is defined simply return the child
            only
              ? onlyOrWarning(only, child, idx, item.props)
              : not ? notOrWarning(only, child, idx, item.props) : child
        )
        if (required && children.length === 0) {
          return (
            <Element key={`item__${i}`} data-slot={item.props.name}>
              <Warning {...item.props} missing />
            </Element>
          )
        }
        return (
          <Element key={`item__${i}`} data-slot={item.props.name}>
            {children}
          </Element>
        )
      })
      .filter(x => Boolean(x))

    return (
      <Slide {...this.props}>
        {filledStatics}
        {filledSlots}
      </Slide>
    )
  }
}

Master.propTypes = {
  children: (props, propName, componentName) => {
    let error
    const prop = props[propName]
    Children.forEach(prop, child => {
      if ([Static, Slot].indexOf(child.type) < 0) {
        error = new Error(
          `"Master" only accepts children of type "Slot" or "Static"`
        )
      }
    })
    return error
  },
  content: PropTypes.node
}

export default Master

import React from 'react'
import PropTypes from 'prop-types'

const Warning = props => {
  const {not, only, invalid, missing, type} = props
  let message
  let onlyMessage
  let notMessage

  if (only) {
    onlyMessage = (
      <div>
        <strong>Allowed components:</strong>
        <ul>
          {only.map((component, i) => (
            <li key={`warning_item__${i}`}>
              {component.displayName || component.name}
            </li>
          ))}
        </ul>
      </div>
    )
  }

  if (not) {
    notMessage = (
      <div>
        <strong>Forbidden components:</strong>
        <ul>
          {not.map((component, i) => (
            <li key={`warning_item__${i}`}>
              {component.displayName || component.name}
            </li>
          ))}
        </ul>
      </div>
    )
  }

  if (missing) {
    message = (
      <div>
        <strong>Error: Required slot</strong>
        {notMessage}
        {onlyMessage}
      </div>
    )
  } else if (invalid) {
    message = (
      <div>
        <strong>Error: Invalid component</strong>
        <p>Tried to use {type}</p>
        {notMessage}
        {onlyMessage}
      </div>
    )
  }
  return <div>{message}</div>
}

Warning.propTypes = {
  not: PropTypes.array,
  only: PropTypes.array,
  type: PropTypes.string,
  invalid: PropTypes.bool,
  missing: PropTypes.bool
}

export default Warning

import {Component} from 'react'
import PropTypes from 'prop-types'

class Paging extends Component {
  constructor(props) {
    super(props)
    this.goToPage = this.goToPage.bind(this)
  }

  componentWillMount() {
    window.addEventListener(this.props.trigger, this.goToPage)
  }

  componentWillUnmount() {
    window.removeEventListener(this.props.trigger, this.goToPage)
  }

  goToPage({which}) {
    const {pages} = this.props
    const {store} = this.context
    const {
      page,
      goToPage,
      fragmentCount,
      fragmentHosts,
      goToFragment,
      setFragment
    } = store
    const {length = 0} = fragmentHosts[page]
    const previousFragment = Math.max(0, fragmentCount - 1)
    const nextFragment = Math.min(length - 1, fragmentCount + 1)
    const previousPage = Math.max(0, page - 1)
    const nextPage = Math.min(pages - 1, page + 1)

    switch (which) {
      case 39:
        ;(() => {
          if (length && nextFragment > fragmentCount) {
            goToFragment(nextFragment)
          } else if (nextPage !== page) {
            goToPage(nextPage)
            goToFragment(0)
          }
        })()
        break
      case 37:
        ;(() => {
          if (length && previousFragment < fragmentCount) {
            goToFragment(previousFragment)
          } else if (previousPage !== page) {
            goToPage(previousPage)
            goToFragment(fragmentHosts[previousPage].length - 1)
          }
        })()
        break
      default:
        break
    }
  }

  render() {
    return null
  }
}

Paging.propTypes = {
  trigger: PropTypes.oneOf(['keyup', 'keydown']).isRequired,
  pages: PropTypes.number.isRequired
}

Paging.contextTypes = {
  store: PropTypes.object.isRequired
}

export default Paging

import React, {Component} from 'react'
import PropTypes from 'prop-types'
import styled from 'styled-components'
import {Motion, spring} from 'react-motion'

/**
 * A single slide.
 * Renders a slide inside a `react-motion` wrapper.
 * Assigns css-variables to allow various transitions
 *
 */
class Slide extends Component {
  static get childContextTypes() {
    return {
      fragmentHost: PropTypes.number
    }
  }
  getChildContext() {
    return {
      fragmentHost: this.props.pageIndex
    }
  }
  render() {
    const {previous, next, children, springSettings} = this.props
    const springStyle = {
      time: spring(previous || next ? 1 : 0, {
        //stiffness: 100,
        //damping: 20,
        ...springSettings
      })
    }
    return (
      <Motion style={springStyle}>
        {({time}) => {
          const style = {
            '--time': ~~(time * 1000) / 1000
          }
          return (
            <StyledSlide
              background={this.props.background}
              mixin={this.props.animation}
              className={this.props.className}
              current={this.props.current}
              next={next}
              previous={previous}
              toPrevious={this.props.toPrevious}
              toNext={this.props.toNext}
              fromPrevious={this.props.fromPrevious}
              fromNext={this.props.fromNext}
              style={style}>
              {children}
            </StyledSlide>
          )
        }}
      </Motion>
    )
  }
}

const SlideDirection = styled.div`
  ${props => {
    if (props.fromPrevious) {
      return `
        --direction: -1;
      `
    }
    if (props.fromNext) {
      return `
        --direction: 1;
      `
    }

    if (props.toPrevious) {
      return `
        --direction: -1;
      `
    }
    if (props.toNext) {
      return `
        --direction: 1;
      `
    }

    if (props.previous) {
      return `
        --direction: -1;
      `
    }
    if (props.next) {
      return `
        --direction: 1;
      `
    }
    if (props.current) {
      return `
        --direction: 0;
      `
    }
  }} z-index: ${props => (props.current ? 1 : 0)};
`

